import React from 'react';

const allSkills = [
  "Î¶¨ÎçîÏã≠", "Í∏∞ÌöçÎ†•", "ÏÜåÌÜµ", "ÌòëÏóÖ", "ÍººÍººÌï®",
  "Ï±ÖÏûÑÍ∞ê", "ÏπúÏ†àÌï®", "Ï∞ΩÏùòÎ†•", "Î∂ÑÏÑùÎ†•", "ÎÖºÎ¶¨Î†•",
  "Ïã§ÌñâÎ†•", "ÏãúÍ∞ÑÍ¥ÄÎ¶¨", "Î¨∏Ï†úÌï¥Í≤∞", "Ïó¥Ï†ï", "ÎÅàÍ∏∞",
  "Ï†ÅÏùëÎ†•", "Î∞úÌëúÎ†•", "Í≥µÍ∞êÎ†•", "Ï†ÑÎûµÏ†Å ÏÇ¨Í≥†", "ÏûêÍ∏∞Ï£ºÎèÑÏÑ±"
];

const SkillManager = ({ skills, setSkills, mainRole, setMainRole, subRole, setSubRole, disabled = false }) => {
  const toggleSkill = (skill) => {
    if (disabled) return; // üîí Î≤ÑÌäº ÌÅ¥Î¶≠ ÎßâÍ∏∞

    if (skills.includes(skill)) {
      setSkills(skills.filter(s => s !== skill));
    } else {
      if (skills.length >= 3) {
        alert("Ïó≠Îüâ ÌÇ§ÏõåÎìúÎäî ÏµúÎåÄ 3Í∞úÍπåÏßÄ ÏÑ†ÌÉùÌï† Ïàò ÏûàÏñ¥Ïöî!");
        return;
      }
      setSkills([...skills, skill]);
    }
  };

  return (
    <div
      style={{
        marginTop: '1rem',
        padding: '0 1rem',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        width: '100%',
        boxSizing: 'border-box'
      }}
    >
      <h4 style={{
        fontFamily: "'Montserrat', 'Noto Sans KR'",
        fontWeight: 600,
        marginBottom: '0.5rem'
      }}>
        ÎÇòÏùò Ïó≠Îüâ ÌÇ§ÏõåÎìú (ÏµúÎåÄ 3Í∞ú ÏÑ†ÌÉù)
      </h4>

      <div style={{
        display: 'flex',
        flexWrap: 'wrap',
        gap: '0.5rem',
        marginBottom: '1.5rem',
        width: '100%'
      }}>
        {allSkills.map(skill => (
          <button
            key={skill}
            onClick={() => toggleSkill(skill)}
            disabled={disabled} // üîí Î≤ÑÌäº ÎπÑÌôúÏÑ±Ìôî
            style={{
              padding: '0.5rem 1rem',
              borderRadius: '20px',
              border: '1px solid #ccc',
              backgroundColor: skills.includes(skill) ? '#FF6B35' : '#F0F0F0',
              color: skills.includes(skill) ? 'white' : '#333',
              cursor: disabled ? 'not-allowed' : 'pointer',
              fontSize: '0.9rem',
              transition: 'all 0.2s',
              opacity: disabled ? 0.6 : 1,
              fontFamily: "'Noto Sans KR', 'Montserrat'"
            }}
          >
            {skill}
          </button>
        ))}
      </div>

      <div style={{
        display: 'flex',
        flexDirection: 'column',
        gap: '1.2rem',
        width: '100%'
      }}>
        <div>
          <label style={{ fontWeight: 600 }}>Ìù¨Îßù Ïó≠Ìï†Íµ∞</label>
          <input
            type="text"
            placeholder="Ïòà: ÌîÑÎ°úÏ†ùÌä∏ Îß§ÎãàÏ†Ä, Î∞úÌëú Îã¥Îãπ"
            value={mainRole}
            onChange={(e) => setMainRole(e.target.value)}
            disabled={disabled} // üîí ÏûÖÎ†•Ï∞Ω ÎπÑÌôúÏÑ±Ìôî
            style={{
              width: '100%',
              padding: '0.7rem',
              marginTop: '0.5rem',
              border: '1px solid #ccc',
              borderRadius: '6px',
              fontSize: '0.95rem',
              boxSizing: 'border-box',
              backgroundColor: disabled ? '#f5f5f5' : 'white'
            }}
          />
        </div>

        <div>
          <label style={{ fontWeight: 600 }}>Î≥¥Ï°∞ Í∞ÄÎä• Ïó≠Ìï†Íµ∞</label>
          <input
            type="text"
            placeholder="Ïòà: ÎîîÏûêÏù∏ ÏóêÎîîÌÑ∞, Ï°∞ÏÇ¨ Îã¥Îãπ"
            value={subRole}
            onChange={(e) => setSubRole(e.target.value)}
            disabled={disabled} // üîí ÏûÖÎ†•Ï∞Ω ÎπÑÌôúÏÑ±Ìôî
            style={{
              width: '100%',
              padding: '0.7rem',
              marginTop: '0.5rem',
              border: '1px solid #ccc',
              borderRadius: '6px',
              fontSize: '0.95rem',
              boxSizing: 'border-box',
              backgroundColor: disabled ? '#f5f5f5' : 'white'
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default SkillManager;
