import React, { useState, useEffect, useRef } from 'react';
import Modal from '@mui/material/Modal';
import CloseIcon from '@mui/icons-material/Close';
import SkillManager from './SkillManager';
import TeamList from './TeamList';
import FeedbackModal from './FeedbackModal';
import { calculateDday } from '../utils/dateUtils';
import GroupsIcon from '@mui/icons-material/Groups';
import { toast } from 'react-toastify';


const ContestModal = ({
  open,
  onClose,
  selectedContest,
  users,
  setUsers,
  userSkills,
  setUserSkills,
  feedbacks,
  onFeedback,
  currentUser
}) => {
  const [mainRole, setMainRole] = useState('');
  const [subRole, setSubRole] = useState('');
  const [matched, setMatched] = useState([]);  // ‚úÖ ÌåÄ Î™©Î°ù ÏÉÅÌÉú Ï†ïÏùò
  const [isFeedbackModalOpen, setIsFeedbackModalOpen] = useState(false);
  const hasShownToast = useRef(false);
  const formRef = useRef(null);
  const queueRef = useRef(null); 
  const [isHovered, setIsHovered] = useState(false);
  const [isTeamHovered, setIsTeamHovered] = useState(false);
  const [isFeedbackHovered, setIsFeedbackHovered] = useState(false);
  const [isTeamroomHovered, setIsTeamroomHovered] = useState(false);

  const scrollToBoth = () => {
  formRef.current?.scrollIntoView({ behavior: 'smooth', block: 'center' });
  queueRef.current?.scrollIntoView({ behavior: 'smooth', block: 'start' });
};
  useEffect(() => {
    const alreadySaved = users.some(u => u.id === currentUser?.id);
    if (open && !hasShownToast.current && !alreadySaved) {
    toast.info("Í≥µÎ™®Ï†Ñ ÏûÖÎ†•ÎûÄ(Ïó≠Îüâ ÌÇ§ÏõåÎìú, Ïó≠Ìï†Íµ∞ Îì±)ÏùÑ Î®ºÏ†Ä ÏûëÏÑ±Ìï¥Ï£ºÏÑ∏Ïöî!");
    hasShownToast.current = true;
    }
  }, [open, users, currentUser]);
  const isMatched = matched.some(team =>
    team.some(member => member.id === currentUser?.id)
  );

// ‚úÖ Ïû¨Îß§Ïπ≠ Ìï®Ïàò
const handleRematch = () => {
  const myTeam = matched.find(team =>
    team.some(member => member.id === currentUser?.id)
  );
  if (!myTeam) return;

  const agreedUsers = myTeam.filter(member =>
    feedbacks[member.id] === 'üëç'
  );

  if (agreedUsers.length < 2) {
    toast.warning("ÎèôÏùòÌïú Ïù∏ÏõêÏù¥ ÎÑàÎ¨¥ Ï†ÅÏñ¥Ïöî! Ïû¨Îß§Ïπ≠Ïù¥ Ïñ¥Î†§ÏõåÏöî.");
    return;
  }

  // ‚õî Ïû¨Îß§Ïπ≠ Î°úÏßÅ Ï†úÍ±∞, Ï∂îÌõÑ Î∞±ÏóîÎìú API Ïó∞ÎèôÏúºÎ°ú ÎåÄÏ≤¥ ÏòàÏ†ï
  /*
  const unmatchedUsers = users.filter(u =>
    !matched.some(team => team.some(m => m.id === u.id))
  );
  const needed = 4 - agreedUsers.length;
  const shuffled = [...unmatchedUsers].sort(() => Math.random() - 0.5);
  const selected = shuffled.slice(0, needed);
  const newTeam = [...agreedUsers, ...selected];

  if (newTeam.length === 4) {
    setMatched(prev => [
      ...prev.filter(team => team !== myTeam),
      newTeam
    ]);
    setUsers(prev => prev.filter(u => !selected.some(s => s.id === u.id)));
    toast.success("Ïû¨Îß§Ïπ≠ ÏôÑÎ£å!");
  } else {
    toast.warning("Ïû¨Îß§Ïπ≠ Ïù∏ÏõêÏù¥ Î∂ÄÏ°±Ìï©ÎãàÎã§.");
  }
  */
};


// ‚úÖ ÎπÑÎèôÏùò Ïù∏Ïõê ÎåÄÍ∏∞Ïó¥Î°ú Ïù¥Îèô
const handleRequeue = () => {
  const myTeam = matched.find(team =>
    team.some(member => member.id === currentUser?.id)
  );
  if (!myTeam) return;

  const disagreedUsers = myTeam.filter(member =>
    feedbacks[member.id] === 'üëé'
  );

  if (disagreedUsers.length === 0) {
    toast.info("ÎπÑÎèôÏùòÏûêÍ∞Ä ÏóÜÏäµÎãàÎã§.");
    return;
  }

  // ‚õî ÌîÑÎ°†Ìä∏ÏóêÏÑú ÏßÅÏ†ë ÎåÄÍ∏∞Ïó¥ Î≥µÍ∑Ä Î°úÏßÅ Ï†úÍ±∞
  /*
  setUsers(prev => [
    ...prev.filter(u => !disagreedUsers.some(d => d.id === u.id)),
    ...disagreedUsers
  ]);

  setMatched(prev =>
    prev.filter(team => team !== myTeam)
  );
  */

  toast.info("ÎπÑÎèôÏùòÏûêÍ∞Ä ÎåÄÍ∏∞Ïó¥Î°ú Ïù¥ÎèôÌï† ÏòàÏ†ïÏûÖÎãàÎã§."); // Ï∂îÌõÑ API Ïó∞Îèô ÌõÑ Ï†ÅÏö©
};


  const handleSave = () => {
  if (isMatched) {
    toast.warning("Ïù¥ÎØ∏ ÌåÄÏóê ÏÜçÌï¥ ÏûàÏñ¥ ÏàòÏ†ïÌï† Ïàò ÏóÜÏäµÎãàÎã§.");
    return;
  }

  if (userSkills.length === 0 || !mainRole.trim()) {
    toast.warning("Ïó≠Îüâ ÌÇ§ÏõåÎìúÏôÄ Ìù¨Îßù Ïó≠Ìï†Íµ∞ÏùÑ Î™®Îëê ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
    return;
  }

  if (!currentUser || !currentUser.id) {
    toast.error("ÌòÑÏû¨ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.");
    return;
  }

  const newUser = {
    id: currentUser.id,
    name: currentUser.name || "ÎÇò",
    skills: userSkills,
    keywords: userSkills,
    mainRole,
    subRole,
    rating: currentUser.rating ?? null,
    participation: currentUser.participation ?? 0
  };

  const alreadyInQueue = users.some(user => user.id === currentUser.id);
 // ‚õî Ïù¥ÌõÑ Î∞±ÏóîÎìú APIÎ°ú ÎåÄÏ≤¥ ÏòàÏ†ï (ÏßÄÍ∏àÏùÄ Î°úÏª¨ users Î∞∞Ïó¥Î°ú Ïú†ÏßÄ)
  if (alreadyInQueue) {
    setUsers(prev =>
      prev.map(user =>
        user.id === currentUser.id ? newUser : user
      )
    );
    toast.info("Í∏∞Ï°¥ Ï†ïÎ≥¥Î•º ÏàòÏ†ïÌñàÏñ¥Ïöî.");
  } else {
    setUsers(prev => [...prev, newUser]);
    toast.success("Ï†ÄÏû• ÏôÑÎ£å! ÎåÄÍ∏∞Ïó¥Ïóê Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§.");
  }
};

 const matchTeam = () => {
  // ÎåÄÍ∏∞ Ïù∏ÏõêÏù¥ Î∂ÄÏ°±Ìï† Í≤ΩÏö∞ ÏïàÎÇ¥ Î©îÏãúÏßÄ Ï∂úÎ†• , ÌåÄÎß§Ïπ≠1Î≤à Î°úÏßÅ apiÎ°ú Î∂àÎü¨ÏôÄÏïºÌï®
  if (users.length < 4) {
    toast.info("ÎåÄÍ∏∞ Ïù∏ÏõêÏù¥ Î∂ÄÏ°±Ìï¥Ïöî! ÌåÄÏóÖÏùÑ Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî üòä");
    return;
  }

  // ‚õî ÌîÑÎ°†Ìä∏ ÎûúÎç§ Îß§Ïπ≠ Î°úÏßÅÏùÄ Ï†úÍ±∞
  /*
  const shuffled = [...users].sort(() => Math.random() - 0.5);
  const teamSize = 4;
  const teams = [];

  for (let i = 0; i < Math.floor(shuffled.length / teamSize) * teamSize; i += teamSize) {
    teams.push(shuffled.slice(i, i + teamSize));
  }

  const matchedIds = teams.flat().map(user => user.id);
  const remaining = shuffled.filter(user => !matchedIds.includes(user.id));
  setMatched(prev => [...prev, ...teams]);
  setUsers(remaining);
  toast.success("ÌåÄ Îß§Ïπ≠ ÏôÑÎ£å!");
  */
};


  if (!selectedContest) return null;

  const {
    title,
    image,
    category,
    deadline,
    start,
    organizer
  } = selectedContest;

  const myTeam = matched.find(team =>
    team.some(member => member.id === currentUser?.id)
  );

  return (
    <>
      <Modal open={open} onClose={onClose}>
        <div style={{
          width: '100vw',
          height: '100vh',
          backgroundColor: 'white',
          display: 'flex',
          flexDirection: 'column'
        }}>
          <div style={{ display: 'flex', justifyContent: 'flex-end', padding: '1rem' }}>
            <button onClick={onClose} style={{
              background: 'none',
              border: 'none',
              fontSize: '1.5rem',
              cursor: 'pointer'
            }}>
              <CloseIcon />
            </button>
          </div>

          <div style={{
            display: 'flex',
            flex: 1,
            overflow: 'hidden',
            padding: '0 2rem 2rem 2rem',
            gap: '2rem'
          }}>
            <div style={{ width: '40%', overflowY: 'auto' }}>
              <img
                src={image}
                alt="Í≥µÎ™®Ï†Ñ"
                style={{ width: '100%', borderRadius: '8px', marginBottom: '1rem' }}
              />
              <h2 style={{ fontFamily: "'Montserrat', 'Noto Sans KR'", fontWeight: 800 }}>{title}</h2>
              <div style={{
                background: '#F8F9FA',
                padding: '1rem',
                borderRadius: '8px'
              }}>
                <p>‚Ä¢ Ï£ºÏµú: {organizer}</p>
                <p>‚Ä¢ ÏùºÏ†ï: {start} ~ {deadline}</p>
                <p>‚Ä¢ ÎßàÍ∞ê: {calculateDday(deadline)}</p>
                <p>‚Ä¢ Î∂ÑÏïº: {category}</p>
              </div>

              <div ref={formRef} style={{ marginTop: '1rem' }}>
                <SkillManager
                  skills={userSkills}
                  setSkills={setUserSkills}
                  mainRole={mainRole}
                  setMainRole={setMainRole}
                  subRole={subRole}
                  setSubRole={setSubRole}
                  disabled={isMatched}
                />
                {isMatched && (
                  <p style={{ color: '#999', fontSize: '0.9rem', marginTop: '0.5rem' }}>
                    ‚ö† ÌåÄÏóê ÏÜçÌïú ÏÉÅÌÉúÏóêÏÑúÎäî ÏûÖÎ†•ÏùÑ ÏàòÏ†ïÌï† Ïàò ÏóÜÏäµÎãàÎã§.
                  </p>
                )}
              <button
                  onClick={handleSave}
                  disabled={isMatched}
                  onMouseEnter={() => setIsHovered(true)}
                  onMouseLeave={() => setIsHovered(false)}
                  style={{
                    width: '100%',
                    padding: '1rem',
                    backgroundColor: isMatched
                      ? '#ccc'
                      : isHovered
                      ? '#ff824e' // hover Ïãú Î∞ùÏùÄ Ïò§Î†åÏßÄ
                      : '#FF6B35',
                    color: 'white',
                    border: 'none',
                    borderRadius: '10px',
                    fontSize: '1.1rem',
                    fontWeight: 700,
                    marginTop: '1.5rem',
                    fontFamily: "'Montserrat', 'Noto Sans KR'",
                    cursor: isMatched ? 'not-allowed' : 'pointer',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    boxShadow: isMatched ? 'none' : '0 2px 6px rgba(0, 0, 0, 0.15)',
                    transition: 'all 0.2s ease-in-out',
                    transform: isHovered && !isMatched ? 'translateY(-1px)' : 'translateY(0)',
                  }}
                >
                  Save!
                </button>

              </div>
            </div>

            <div style={{ width: '60%', overflowY: 'auto' }}>
              <h2 style={{
                color: '#FF6B35',
                fontFamily: "'Montserrat', 'Noto Sans KR'",
                fontWeight: 800
              }}>
                <GroupsIcon style={{ marginRight: '0.5rem' }} />
                Ìï®ÍªòÌïòÏûê ÌåÄÏúºÎ°ú!
              </h2>

         
              <div ref={queueRef} style={{
                listStyle: 'none',
                padding: 0,
                maxHeight: '200px',
                overflowY: 'auto',
                marginBottom: '1rem',
                background: '#FFF9F7',
                borderRadius: '8px',
                paddingInline: '1rem'
              }}>
                {users.map(user => (
                  <li key={user.id} style={{
                    display: 'flex',
                    alignItems: 'center',
                    padding: '0.8rem 0',
                    borderBottom: '1px solid #eee',
                    fontWeight: 500,
                    fontSize: '1rem',
                    color: '#333'
                  }}>
                    
                  üë§  {user.name}
                  </li>
                ))}
                </div>
                          {/* ÌåÄÏóÖ Î≤ÑÌäº ÎòêÎäî ÏïàÎÇ¥ Î©îÏãúÏßÄ */}
              {myTeam ? (
                <p style={{
                  maxWidth: '100%',    
                  width: '100%',
                  padding: '1rem',
                  background: '#FFF3ED',
                  color: '#FF6B35',
                  borderRadius: '8px',
                  textAlign: 'center',
                  fontWeight: 600,
                  fontSize: '1rem',
                  border: '1px solid #FF6B35',
                  boxSizing: 'border-box'       // ‚úÖ Ìå®Îî© Ìè¨Ìï®Ìï¥ÏÑú ÎÑàÎπÑ Í≥ÑÏÇ∞
                }}>
                  Ïù¥ÎØ∏ ÌåÄÏóê ÏÜçÌï¥ ÏûàÏñ¥Ïöî! Í≤∞Í≥ºÎ•º Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî üòä
                </p>
              ) : (
             <button
              onClick={matchTeam}
              onMouseEnter={() => setIsTeamHovered(true)}
              onMouseLeave={() => setIsTeamHovered(false)}
              style={{
                width: '100%',
                padding: '1rem',
                backgroundColor: isTeamHovered ? '#ff824e' : '#FF6B35', // hover Ïãú Î∞ùÏùÄ Ï£ºÌô©
                color: 'white',
                border: 'none',
                borderRadius: '10px',
                cursor: 'pointer',
                fontSize: '1.1rem',
                fontWeight: 700,
                marginBottom: '1rem',
                fontFamily: "'Montserrat', 'Noto Sans KR'",
                boxShadow: '0 2px 6px rgba(0, 0, 0, 0.15)',
                transition: 'all 0.2s ease-in-out',
                transform: isTeamHovered ? 'translateY(-1px)' : 'translateY(0)',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center'
              }}
            >
              <GroupsIcon style={{ marginRight: '0.5rem' }} />
              TEAM UP!
            </button>
              )}
       
              {matched.length > 0 && (
                <div style={{ marginTop: '1rem' }}>
                  <TeamList
                    matched={matched}
                    feedbacks={feedbacks}
                    onFeedback={onFeedback}
                    currentUser={currentUser}
                  />
                  <div style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    gap: '1rem',
                    marginTop: '1.5rem',
                    paddingBottom: '1rem'
                  }}>
                    <button
                    onMouseEnter={() => setIsFeedbackHovered(true)}
                    onMouseLeave={() => setIsFeedbackHovered(false)}
                    onClick={() => setIsFeedbackModalOpen(true)}
                    style={{
                      flex: 1,
                      padding: '0.9rem', // Í∏∞Ï°¥ ÌÅ¨Í∏∞ Ïú†ÏßÄ
                      backgroundColor: isFeedbackHovered ? '#ff824e' : '#FF6B35',
                      color: 'white',
                      border: 'none',
                      borderRadius: '8px',
                      fontSize: '1rem',
                      fontWeight: 600,
                      fontFamily: "'Montserrat', 'Noto Sans KR'",
                      cursor: 'pointer',
                      display: 'flex',
                      justifyContent: 'center',
                      alignItems: 'center',
                      transition: 'background-color 0.2s ease, transform 0.2s ease',
                      transform: isFeedbackHovered ? 'translateY(-1px)' : 'translateY(0)',
                    }}
                  >
                    ÌîºÎìúÎ∞± Í≤∞Í≥º ÌôïÏù∏ÌïòÍ∏∞
                  </button>
                   <button
                      onMouseEnter={() => setIsTeamroomHovered(true)}
                      onMouseLeave={() => setIsTeamroomHovered(false)}
                      onClick={() => window.open('/TeamPage', '_blank')}
                      style={{
                        flex: 1,
                        padding: '0.9rem', // Í∏∞Ï°¥ ÏÑ∏Î°ú ÌÅ¨Í∏∞ Ïú†ÏßÄ
                        backgroundColor: isTeamroomHovered ? '#ff824e' : '#FF6B35',
                        color: 'white',
                        border: 'none',
                        borderRadius: '8px',
                        fontSize: '1rem',
                        fontWeight: 600,
                        fontFamily: "'Montserrat', 'Noto Sans KR'",
                        cursor: 'pointer',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                        transition: 'background-color 0.2s ease, transform 0.2s ease',
                        transform: isTeamroomHovered ? 'translateY(-1px)' : 'translateY(0)',
                      }}
                    >
                      ÌåÄÎ£∏ÏúºÎ°ú Ïù¥ÎèôÌïòÍ∏∞
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </Modal>

      <FeedbackModal
        open={isFeedbackModalOpen}
        onClose={() => setIsFeedbackModalOpen(false)}
        team={myTeam || []}
        feedbacks={feedbacks}
        currentUser={currentUser}
        scrollToBoth={scrollToBoth}
        onRematch={handleRematch}       // ‚úÖ Ï∂îÍ∞Ä
        onRequeue={handleRequeue}       // ‚úÖ Ï∂îÍ∞Ä
        users={users}
      />
    </>
  );
};

export default ContestModal;
