import React from 'react';
import PersonIcon from '@mui/icons-material/Person';

const TeamList = ({ matched, feedbacks, onFeedback, currentUser }) => {
  // ÌòÑÏû¨ Ïú†Ï†ÄÍ∞Ä ÏÜçÌïú ÌåÄÏùÑ Ï∞æÏùå
  const myTeamIndex = matched.findIndex(team =>
    team.some(member => member.id === currentUser?.id)
  );

  const myTeam = myTeamIndex !== -1 ? matched[myTeamIndex] : null;

  // ÌòÑÏû¨ Ïú†Ï†ÄÍ∞Ä ÏÜçÌïú ÌåÄÏõê Î¶¨Ïä§Ìä∏ Ï†ïÎ†¨: currentUser ‚Üí ÎÇòÎ®∏ÏßÄ ÌåÄÏõê
  const sortedMyTeam = myTeam
    ? [...myTeam].sort((a, b) => (a.id === currentUser?.id ? -1 : b.id === currentUser?.id ? 1 : 0))
    : [];

  // ÎÇòÎ®∏ÏßÄ ÌåÄ Î™©Î°ù (ÎÇ¥ ÌåÄ Ï†úÏô∏)
  const otherTeams = matched.filter((_, idx) => idx !== myTeamIndex);
  
  // Ïã†Í∑ú Ïú†Ï†Ä
 

  // ÌåÄ Î†åÎçîÎßÅ Ìï®Ïàò
  const renderTeam = (team, index, isMyTeam = false) => {
    const sortedTeam = [...team].sort((a, b) => {
      if (a.id === currentUser?.id) return -1;
      if (b.id === currentUser?.id) return 1;
      return 0;
    });

    return (
      <div
        key={index}
        style={{
          border: '2px solid #ccc',
          borderRadius: '10px',
          padding: '1rem',
          marginBottom: '1.5rem',
          backgroundColor: isMyTeam ? '#FFF1EC' : 'white'
        }}
      >
        <h3 style={{
          fontWeight: 700,
          fontSize: '1.1rem',
          color: isMyTeam ? '#FF6B35' : '#333'
        }}>
          {isMyTeam ? '‚≠ê ÎÇ¥ ÌåÄ Ï†ïÎ≥¥' : `${index + 1}ÌåÄ`}
        </h3>

        <ul style={{ listStyle: 'none', padding: 0 }}>
          {sortedTeam.map(member => (
            <li
              key={member.id}
              style={{
                padding: '1rem 0',
                borderBottom: '1px solid #eee',
                display: 'flex',
                flexDirection: 'column',
                gap: '0.3rem'
              }}
            >
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <PersonIcon style={{ marginRight: '0.3rem', color: '#444' }} />
                <strong style={{ fontSize: '1.05rem' }}>
                  {member.name}{member.id === currentUser?.id ? "" : ""}
                </strong>
              </div>

              <div style={{ paddingLeft: '1.5rem' }}>
                <p><strong>Ìù¨Îßù Ïó≠Ìï†Íµ∞:</strong> {member.mainRole || 'ÏûÖÎ†• ÏóÜÏùå'}</p>
                <p><strong>Î≥¥Ï°∞ Í∞ÄÎä• Ïó≠Ìï†Íµ∞:</strong> {member.subRole || 'ÏûÖÎ†• ÏóÜÏùå'}</p>
                <p><strong>Î≥¥Ïú† Ïó≠Îüâ:</strong> {member.keywords?.join(', ') || 'ÏóÜÏùå'}</p>

                {member.rating !== undefined && member.participation !== undefined ? (
                  <p style={{ marginTop: '0.3rem', color: '#666' }}>
                    ‚≠ê{member.rating.toFixed(1)} ({member.participation}Ìöå Ï∞∏Ïó¨)
                  </p>
                ) : (
                  <p style={{ color: '#aaa' }}>‚≠ê ÏïÑÏßÅ Î≥ÑÏ†êÏù¥ ÏóÜÏñ¥Ïöî / Ï≤´ Îß§Ïπ≠ ÎåÄÍ∏∞ Ï§ë</p>
                )}
              </div>

           <div style={{ marginTop: '0.3rem', paddingLeft: '1.5rem' }}>
                <button
                  onClick={() => onFeedback(member.id, 'üëç')}
                  style={{
                    marginRight: '0.5rem',
                    backgroundColor: feedbacks[member.id] === 'üëç' ? '#FF6B35' : 'white',
                    color: feedbacks[member.id] === 'üëç' ? 'white' : 'black',
                    border: '1px solid #ccc',
                    borderRadius: '5px',
                    padding: '0.4rem 0.8rem',
                    fontSize: '1rem',
                    cursor: 'pointer',
                    transition: 'all 0.2s',
                  }}
                >
                  üëç
                </button>
                <button
                  onClick={() => onFeedback(member.id, 'üëé')}
                  style={{
                    backgroundColor: feedbacks[member.id] === 'üëé' ? '#FF6B35' : 'white',
                    color: feedbacks[member.id] === 'üëé' ? 'white' : 'black',
                    border: '1px solid #ccc',
                    borderRadius: '5px',
                    padding: '0.4rem 0.8rem',
                    fontSize: '1rem',
                    cursor: 'pointer',
                    transition: 'all 0.2s',
                  }}
                >
                  üëé
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    );
  };

  return (
    <div>
      {myTeam && renderTeam(myTeam, myTeamIndex, true)}
      {otherTeams.map((team, idx) => renderTeam(team, idx))}
    </div>
  );
};

export default TeamList;
