    organizer: "AWS / ÏΩîÎìúÌä∏Î¶¨",
    image: "/aws.png"
  },
  {
    id: 2,
    title: "Ï†ú7Ìöå ÏÑúÏö∏ÍµêÏú° Îç∞Ïù¥ÌÑ∞ Î∂ÑÏÑù¬∑ÌôúÏö© ÏïÑÏù¥ÎîîÏñ¥ Í≥µÎ™®Ï†Ñ",
    description: "ÍµêÏú° Îç∞Ïù¥ÌÑ∞Î•º ÌôúÏö©Ìïú Î∂ÑÏÑù Î∞è ÏãúÍ∞ÅÌôî",
    category: "Îç∞Ïù¥ÌÑ∞/ÏΩîÎî©",
    deadline: "2025-06-01",
    start: "2025-04-21",
    organizer: "ÏÑúÏö∏ÌäπÎ≥ÑÏãúÍµêÏú°Ï≤≠",
    image: "/seoul.png"
  },
  {
    id: 3,
    title: "2025ÎÖÑ Í≤ΩÍ∏∞ÎèÑÏÑúÍ¥Ä ÌÅ¨Î¶¨ÏóêÏù¥Ìã∞Î∏å ÏãúÎÑàÏßÄ Í≥µÎ™®Ï†Ñ",
    description: "Í≥µÍ≥µÎèÑÏÑúÍ¥Ä ÏãúÏä§ÌÖú Í∞úÏÑ† ÏïÑÏù¥ÎîîÏñ¥ Í≥µÎ™®",
    category: "ITÍ∏∞Ìöç/ÌîÑÎ°úÍ∑∏ÎûòÎ∞ç",
    deadline: "2025-06-30",
    start: "2025-04-09",
    organizer: "Í≤ΩÍ∏∞ÎèÑ / Í≤ΩÍ∏∞ÎèÑÏÑúÍ¥Ä",
    image: "/creative.png"
  },
  {
    id: 4,
    title: "2025 GH Í≥µÍ∞ÑÎ≥µÏßÄ Ï≤≠ÎÖÑ Í≥µÎ™®Ï†Ñ",
    description: "Í≥µÍ∞Ñ Í∏∞Ïà† Í∏∞Î∞òÏùò ÏïÑÏù¥ÎîîÏñ¥ Î∞è ÌîÑÎ°úÌÜ†ÌÉÄÏûÖ Í≥µÎ™®",
    category: "Í≥µÍ∞ÑIT/UXÏÑ§Í≥Ñ",
    deadline: "2025-06-29",
    start: "2025-06-02",
    organizer: "Í≤ΩÍ∏∞Ï£ºÌÉùÎèÑÏãúÍ≥µÏÇ¨",
    image: "/gh.png"
  },
  {
    id: 5,
    title: "Ï†ú6Ìöå Îâ¥Ïä§ÏùΩÍ∏∞ Îâ¥Ïä§ÏùºÍ∏∞ Í≥µÎ™®Ï†Ñ",
    description: "Îâ¥Ïä§ Îç∞Ïù¥ÌÑ∞Î•º ÌôúÏö©Ìïú ÏΩòÌÖêÏ∏† Í∏∞Ìöç",
    category: "ÎØ∏ÎîîÏñ¥/ÏΩîÎî©ÍµêÏú°",
    deadline: "2025-07-31",
    start: "2025-04-07",
    organizer: "ÌïúÍµ≠Ïñ∏Î°†ÏßÑÌù•Ïû¨Îã®",
    image: "/news.png"
  }
];

function TeamMatching1() {
  const currentUser = {
    id: 99,
    name: "Ïù¥Î™ÖÏ§Ä",
    rating: 4.8,
    participation: 2
  };
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [openMenus, setOpenMenus] = useState({});
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedContest, setSelectedContest] = useState(null);
  const [userSkills, setUserSkills] = useState([]);
  const [matchedUsers, setMatchedUsers] = useState([]);
  const [feedbacks, setFeedbacks] = useState({});
  const [users, setUsers] = useState([]);   //apiÎ°ú Ïú†Ï†Ä Î∂àÎü¨Ïò§Í∏∞
  
 const handleMatchTeam = () => {}; // Ï∂îÌõÑ API Ïó∞Í≤∞ ÏãúÏ†êÏóêÏÑú Íµ¨ÌòÑ, 4Î™Ö ÎûúÎç§ Îß§Ïπ≠ Î°úÏßÅ

//  const handleMatchTeam = async () => {       // Ï∂îÌõÑ API Ïó∞Í≤∞ ÏãúÏ†ê Ïãú ÌïÑÏöî
//   try {
//     const res = await axios.post('/api/teamup/match', {
//       user_id: currentUser.id,
//       contest_id: selectedContest.id
//     });
//     setMatchedUsers(res.data.teams); // ‚Üê Î∞±ÏóîÎìú ÏùëÎãµ Íµ¨Ï°∞Ïóê Îî∞Îùº ÏàòÏ†ï
//     setUsers(res.data.waiting);      // ‚Üê Ïû¨ÏûÖÏû• ÎåÄÍ∏∞Ïó¥ÎèÑ ÌïÑÏöî Ïãú
//   } catch (err) {
//     console.error("ÌåÄ Îß§Ïπ≠ Ïã§Ìå®", err);
//   }
// };

  const handleFeedback = (userId, type) => {
    setFeedbacks(prev => ({ ...prev, [userId]: type }));
  };

  return (
    <div className="team-matching-container">
      {/* Ìó§Îçî */}
      <header className="team-matching-header">
        <span className="logo">TUP!</span>
        {!drawerOpen && (
          <button
            className="menu-button"
            onClick={() => setDrawerOpen(true)}
            aria-label="Î©îÎâ¥ Ïó¥Í∏∞"
          >
            <MenuIcon style={{ fontSize: '2.2rem', color: '#FF6B35' }} />
          </button>
        )}
      </header>

      {/* ÎìúÎ°úÏñ¥ Î©îÎâ¥ */}
      <DrawerMenu
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        openMenus={openMenus}
        onToggle={setOpenMenus}
      />

      {/* ÏÜåÍ∞ú Î¨∏Íµ¨ */}
      <div className="matching-intro">
        <h1>
          <span className="highlight">AutoTeamUp</span> - Îπ†Î•¥Í≤å ÌåÄ Í≤∞ÏÑ±ÌïòÍ∏∞
        </h1>
        <p>
          Í≥µÎ™®Ï†ÑÏùÑ ÏÑ†ÌÉùÌïú Ï∞∏Í∞ÄÏûêÎì§Ïù¥ ÎûúÎç§ÏúºÎ°ú ÌåÄÏùÑ Í≤∞ÏÑ±Ìïú ÌõÑ, <strong>2Ï∞® ÌîºÎìúÎ∞±</strong>ÏùÑ ÌÜµÌï¥ ÏµúÏ¢Ö ÌåÄÏùÑ ÌôïÏ†ïÌïòÎäî Î∞©ÏãùÏûÖÎãàÎã§
        </p>
      </div>

            {/* Í≥µÎ™®Ï†Ñ Ïπ¥Îìú Î¶¨Ïä§Ìä∏ */}
      <section className="contest-list-section">
        <h3 className="contest-section-title">üì¢ Í≥µÎ™®Ï†ÑÏùÑ Ï∞æÏïÑ ÌåÄÏóÖ ÏßÑÌñâÌïòÍ∏∞</h3>
        <div className="contest-grid">
          {contestList.map(contest => (
            <div
              key={contest.id}
              className="hover-card"
              onClick={() => {
                setSelectedContest(contest);
                setModalOpen(true);
              }}
            >
              <img src={contest.image} alt="Í≥µÎ™®Ï†Ñ" className="hover-image" />
              <div className="hover-details">
                <h3>{contest.title}</h3>
                <p>ÎßàÍ∞ê: {contest.deadline} ({calculateDday(contest.deadline)})</p>
              </div>
            </div>
          ))}
        </div>
      </section>


      {/* Í≥µÎ™®Ï†Ñ Î™®Îã¨ */}
      {selectedContest && (
        <ContestModal
          open={modalOpen}
          onClose={() => setModalOpen(false)}
          selectedContest={selectedContest}
          users={users}
          setUsers={setUsers}  // ‚úÖ Ïù¥ Ï§Ñ Ï∂îÍ∞Ä!
          userSkills={userSkills}
          setUserSkills={setUserSkills}
          matched={matchedUsers}
          matchTeam={handleMatchTeam}
          feedbacks={feedbacks}
          onFeedback={handleFeedback}
          currentUser={currentUser}
        />
      )}
    </div>
  );
}

export default TeamMatching1;
